package Bucles;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero = 0;
		
		while (numero<100) {
			System.out.println("iteracion " + numero);
		numero ++;
		}
		if (100>= numero) {
			System.out.println("es una aberracion");
		}
		System.out.println("fin del bucle");

	}

}
